method.body:
function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(CLO[argobj.pid].div);
				var bDefer = false;
				switch(oCurObj.display)
				{
					case "none":
					{
						bDefer = true;
						CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" }, { sId: argobj.pid, sEvtName: "EVENT_STATE_CHANGED" } ] });
						break;
					}
					case "time":
					case "frame":
					case "slide":
					{
						if((oCurObj.iBegin!=null && +oCurObj.iBegin!=0) || !oCurObj.oParentFrame.bIsFirst)
						{
							bDefer = true;
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" }, { sId: argobj.pid, sEvtName: "EVENT_STATE_CHANGED" } ] });
						}
						else
						{
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_STATE_CHANGED" } ] });
						}
						break;
					}
				}
				oCurObj.data =
				{
					bBtn: (oCurObj.jxParams.children("btn").text()=="yes"),
					bTypeWriter: (oCurObj.jxParams.children("typewriter").text()=="yes" && oCurObj.jxParams.children("content").text()!="image"),
					sDependable: oCurObj.jxParams.children("btn_behavior").text(),
					oHandlers: {},
					sContent: oCurObj.jxParams.children("content").text(),
					sSoundClick: oCurObj.jxParams.children("sound_click").text(),
					sSoundOver: oCurObj.jxParams.children("sound_over").text()
				};
				if(oCurObj.data.sSoundClick=="custom")
				{
					oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
				}
				if(oCurObj.data.sSoundOver=="custom")
				{
					oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
				}
				var sHTML;
				var sNodeName;
				for(var sState in oCurObj.oStates)
				{
					if(oCurObj.data.sContent!="image")
					{
					sNodeName = (oCurObj.oStates[sState].jxParams.children("content").text()=="html") ? "text_main" : "text_plain";
					oCurObj.oStates[sState].sText = (sState=="default") ? oCurObj.jxParams.children(sNodeName).text() : oCurObj.oStates[sState].jxParams.children(sNodeName).text();
					sHTML = CL.Substitute.All({ sString: oCurObj.oStates[sState].sText });
					jCurObjDiv.find("div[data-state='" + sState + "'] .cl-btn-text").html( sHTML );
					}
				}
				if(oCurObj.data.bTypeWriter)
				{
					var sSpeed = oCurObj.jxParams.children("typewriter_speed").text();
					var iSpeed = (sSpeed=="") ? 10 : parseInt(sSpeed, 10);
					if(isNaN(iSpeed)) iSpeed = 10;
					oCurObj.data.iDelay = Math.round(1000/iSpeed);
				}
				var jEventDiv = jCurObjDiv.children(".cl-evt-wrapper");
				if(jEventDiv.length!=0)
				{
					var sBody;
					var sNewFn;
					sBody = jEventDiv[0].getAttribute("onmouseover");
					if(sBody!=null && sBody!="")
					{
						oCurObj.data.oHandlers["mouseover"] = new Function("e", sBody);
						jEventDiv[0].removeAttribute("onmouseover");
					}
					sBody = jEventDiv[0].getAttribute("onmouseout");
					if(sBody!=null && sBody!="")
					{
						oCurObj.data.oHandlers["mouseout"] = new Function("e", sBody);
						jEventDiv[0].removeAttribute("onmouseout");
					}
					sBody = jEventDiv[0].getAttribute("onclick");
					if(sBody!=null && sBody!="")
					{
						oCurObj.data.oHandlers["click"] = new Function("e", sBody);
						jEventDiv[0].removeAttribute("onclick");
					}
					sBody = jEventDiv[0].getAttribute("ondblclick");
					if(sBody!=null && sBody!="")
					{
						oCurObj.data.oHandlers["dblclick"] = new Function("e", sBody);
						jEventDiv[0].removeAttribute("ondblclick");
					}
					sBody = jEventDiv[0].getAttribute("onmousedown");
					if(sBody!=null && sBody!="")
					{
						oCurObj.data.oHandlers["mousedown"] = new Function("e", sBody);
						jEventDiv[0].removeAttribute("onmousedown");
					}
					sBody = jEventDiv[0].getAttribute("onmouseup");
					if(sBody!=null && sBody!="")
					{
						oCurObj.data.oHandlers["mouseup"] = new Function("e", sBody);
						jEventDiv[0].removeAttribute("onmouseup");
					}
				}
				jCurObjDiv.find("div").off().css({ "cursor": "default" });
				jCurObjDiv.find("div[data-state]").each(function ()
				{
					var sState = this.getAttribute("data-state");
					if(sState!="disabled")
					{
						var jxParams = (sState=="default") ? oCurObj.jxParams : oCurObj.oStates[sState].jxParams;
						var sSelector = ".cl-btn-event-shape";
						var jShape = $(this).find(sSelector);
                        if(jShape.length!=0)
                        {
                            if(CL.bTouch)
                            {
                                jShape[0].ontouchstart = function (event) { oCurObj.CallMethod({ sMethod: 'UIEvent', oMethodArgs: { oEvt: event, sEvt: 'touchstart' } }) };
                                jShape[0].ontouchend = function (event) { oCurObj.CallMethod({ sMethod: 'UIEvent', oMethodArgs: { oEvt: event, sEvt: 'touchend' } }) };
                            }
                            else
                            {
                                jShape[0].onclick = function (event) { oCurObj.CallMethod({ sMethod: 'UIEvent', oMethodArgs: { oEvt: event, sEvt: 'click' } }) };
                                jShape[0].onmouseover = function (event) { oCurObj.CallMethod({ sMethod: 'UIEvent', oMethodArgs: { oEvt: event, sEvt: 'over' } }) };
                                jShape[0].onmouseout = function (event) { oCurObj.CallMethod({ sMethod: 'UIEvent', oMethodArgs: { oEvt: event, sEvt: 'out' } }) };
                                jShape[0].onmousedown = function (event) { oCurObj.CallMethod({ sMethod: 'UIEvent', oMethodArgs: { oEvt: event, sEvt: 'down' } }); };
                                jShape[0].onmouseup = function (event) { oCurObj.CallMethod({ sMethod: 'UIEvent', oMethodArgs: { oEvt: event, sEvt: 'up' } }) };
                                jShape[0].ondblclick = function (event) { oCurObj.CallMethod({ sMethod: 'UIEvent', oMethodArgs: { oEvt: event, sEvt: 'dblclick' } }) };
								if(sState!="default")
								{
									jShape.css({ "cursor": "pointer" });
								}
								else
								{
									if(oCurObj.oStates["highlighted"]==null && (oCurObj.data.oHandlers["click"]!=null || oCurObj.data.oHandlers["dblclick"]!=null || oCurObj.data.oHandlers["mousedown"]!=null || oCurObj.data.oHandlers["mouseup"]!=null))
									{
										jShape.css({ "cursor": "pointer" });
									}
								}

							}
						}
					}
					else
					{
						if(oCurObj.data.sBtnType=="html")
						{
							$(this).find(".cl-btn-event-svg").hide();
						}
					}
				});
				oCurObj.data.bCreated = true;
				if(!bDefer)
				{
					oCurObj.CallMethod({ sMethod: "Display" });
				}
				else
				{
					jCurObjDiv.find(".cl-container:first").hide();
				}
				return true;
			}

function HandleEvt(argobj)
{
	var oCurObj = CLO[argobj.pid];
	if(oCurObj==null || oCurObj.div==null) return false;
	switch(argobj.evt)
	{
		case "EVENT_OBJECT_SHOW":
		{
			if(!oCurObj.data.bCreated) return false;
			oCurObj.CallMethod({ sMethod: "Display" });
			break;
		}
		case "EVENT_STATE_CHANGED":
		{
			if(oCurObj.sCurrentState=="active")
			{
				if(oCurObj.sPreviousState!="selected")
				{
					if(oCurObj.oStates["selected"]!=null)
					{
						oCurObj.SetState({ sState: "selected" });
					}
				}
			}
			break;
		}
	}
	return true;
}

function Display(argobj)
{
	var oCurObj = CLO[argobj.pid];
	var jCurObjDiv = $(oCurObj.div);
	jCurObjDiv.find(".cl-container:first").show();
	if(oCurObj.data.bModal)
	{
		CL.Modal({ bOn: true, sSrcId: argobj.pid });
		oCurObj.current.nZ = 5001;
		jCurObjDiv.css({ "z-index": 5001 });
	}
	if(oCurObj.data.bTypeWriter)
	{
		oCurObj.CallMethod({ sMethod: "TypeWriter", oMethodArgs: { oElem: jCurObjDiv.find("div[data-state='default'] .cl-btn-text"), iDelay: oCurObj.data.iDelay, iItem: 0 } });
	}
	return true;
}

function TypeWriter(argobj)
{
	var oCurObj = CLO[argobj.pid];
	var jCurObjDiv = $(oCurObj.div);
	var jElem = $(argobj.oElem);
	if(jElem.attr("data-wrapped")=="yes")
	{
		jElem.find(".cl-char").each(function () { $(this).contents().unwrap() });
	}
	jElem.attr({ "data-wrapped": "yes" }).html( CL.Common.WrapCharacters({ sHTML: jElem.html() }) );
	oCurObj.CallMethod({ sMethod: "NextChar", oMethodArgs: { oElem: jElem, iDelay: oCurObj.data.iDelay, iItem: 0 } });
	return true;
}

function NextChar(argobj)
{
	if(argobj==null) return false;
	if(argobj.pid==null || argobj.oElem==null || argobj.iDelay==null || argobj.iItem==null)
	{
		return false;
	}
	var oCurObj = CLO[argobj.pid];
	if(oCurObj==null)
	{
		return false;
	}
	var jElem = $(argobj.oElem);
	var jItem = jElem.find(".cl-char[data-i='" + argobj.iItem + "']");
	if(jItem.length==0)
	{
		oCurObj.FireEvent({ sName: "onTypeWriterEnd" });
		return false;
	}
	jItem.contents().unwrap();
	oCurObj.iTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "NextChar", oMethodArgs: { oElem: argobj.oElem, iDelay: argobj.iDelay, iItem: (argobj.iItem+1) } }); }, argobj.iDelay);
	return true;
}

function UIEvent(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(CLO[argobj.pid].div);
				if(oCurObj.sCurrentState!="disabled")
				{
					switch(argobj.sEvt)
					{
						case "over":
						{
							CL.Sound.Stop({ sId: argobj.pid, sEvent: "over" });
							if(oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
							}
							if(oCurObj.data.oHandlers["mouseover"]!=null)
							{
								oCurObj.data.oHandlers["mouseover"](argobj.oEvt);
							}
							if(oCurObj.sCurrentState=="default" && oCurObj.oStates["highlighted"]!=null)
							{
								oCurObj.SetState({ sState: "highlighted" });
							}
							break;
						}
						case "out":
						{
							if(oCurObj.data.oHandlers["mouseout"]!=null)
							{
								oCurObj.data.oHandlers["mouseout"](argobj.oEvt);
							}
							if(oCurObj.sCurrentState=="highlighted")
							{
								oCurObj.SetState({ sState: "default" });
							}
							break;
						}
                        case "down":
                        {
                           if(oCurObj.oStates["active"]!=null)
                            {
								if(oCurObj.data.oHandlers["click"]!=null)
								{
									// defer state change until click event fired
									oCurObj.data.bDeferActiveState = true;
								}
								else
								{
									oCurObj.sPrevState = oCurObj.sCurrentState;
									oCurObj.SetState({ sState: "active" });
								}
                            }
                            if(oCurObj.data.oHandlers["mousedown"]!=null)
                            {
                                oCurObj.data.oHandlers["mousedown"](argobj.oEvt);
                            }
                           break;
                        }
						case "up":
						{
							if(oCurObj.data.oHandlers["mouseup"]!=null)
							{
								oCurObj.data.oHandlers["mouseup"](argobj.oEvt);
							}
							break;
						}
						case "click":
						{
                            if(oCurObj.data.bDeferActiveState==true)
							{
								oCurObj.data.bDeferActiveState = false;
								oCurObj.sPrevState = oCurObj.sCurrentState;
								oCurObj.SetState({ sState: "active" });
							}
							CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
							if(oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
							}
							//argobj.oEvt.stopImmediatePropagation();
							if(oCurObj.data.oHandlers["click"]!=null)
							{
								oCurObj.data.oHandlers["click"](argobj.oEvt);
							}
							if(oCurObj.oStates["selected"]!=null)
							{
								if(oCurObj.sCurrentState=="selected" || (oCurObj.sCurrentState=="active" && oCurObj.sPrevState=="selected"))
								{
									oCurObj.sPrevState = "";
									oCurObj.SetState({ sState: "default" });
								}
								else
								{
									oCurObj.SetState({ sState: "selected" });
									if(oCurObj.data.sDependable=="dep")
									{
										var sObjectId;
										for(var i=0; i<CLF[oCurObj.sFrameId].jxObjects.length; i++)
										{
											sObjectId = CLF[oCurObj.sFrameId].jxObjects[i].getAttribute("id");
											if(CLO[sObjectId]!=oCurObj && CLO[sObjectId].sType==oCurObj.sType)
											{
												if(CLO[sObjectId].sCurrentState!="default")
												{
													CLO[sObjectId].SetState({ sState: "default" })
												}
											}
										}
									}
									else if(oCurObj.data.sDependable=="group")
									{
										var sGroupId = oCurObj.jxParams.children("dep_group").text();
										var jxGroupRefs = CL.jxGroups.find("group[id='" + sGroupId + "'] > ref");
										jxGroupRefs.each(function ()
										{
											var sObjectId = $(this).attr("id");
											if(CLO[sObjectId]!=oCurObj && CLO[sObjectId].sType==oCurObj.sType)
											{
												if(CLO[sObjectId].sCurrentState!="default")
												{
													CLO[sObjectId].SetState({ sState: "default" });
												}
											}
										});
									}
								}
							}
							else
							{
								oCurObj.SetState({ sState: "default" });
							}
							break;
						}
						case "dblclick":
						{
							if(oCurObj.data.oHandlers["dblclick"]!=null)
							{
								oCurObj.data.oHandlers["dblclick"](argobj.oEvt);
							}
							break;
						}
						case "touchstart":
						{
							if(oCurObj.data.oHandlers["mousedown"]!=null)
							{
								oCurObj.data.oHandlers["mousedown"](argobj.oEvt);
							}
							if(oCurObj.oStates["active"]!=null)
							{
								oCurObj.sPrevState = oCurObj.sCurrentState;
								oCurObj.SetState({ sState: "active" });
							}
							break;
						}
						case "touchend":
						{
							if(oCurObj.data.oHandlers["click"]!=null)
							{
								oCurObj.data.oHandlers["click"](argobj.oEvt);
							}
							if(oCurObj.data.oHandlers["mouseup"]!=null)
							{
								oCurObj.data.oHandlers["mouseup"](argobj.oEvt);
							}
							if(oCurObj.oStates["selected"]!=null)
							{
								if(oCurObj.sCurrentState=="selected" || (oCurObj.sCurrentState=="active" && oCurObj.sPrevState=="selected"))
								{
									oCurObj.sPrevState = "";
									oCurObj.SetState({ sState: "default" });
								}
								else
								{
									oCurObj.SetState({ sState: "selected" });
									if(oCurObj.data.sDependable=="dep")
									{
										var sObjectId;
										for(var i=0; i<CLF[oCurObj.sFrameId].jxObjects.length; i++)
										{
											sObjectId = CLF[oCurObj.sFrameId].jxObjects[i].getAttribute("id");
											if(CLO[sObjectId]!=oCurObj && CLO[sObjectId].sType==oCurObj.sType)
											{
												if(CLO[sObjectId].sCurrentState!="default")
												{
													CLO[sObjectId].SetState({ sState: "default" })
												}
											}
										}
									}
									else if(oCurObj.data.sDependable=="group")
									{
										var sGroupId = oCurObj.jxParams.children("dep_group").text();
										var jxGroupRefs = CL.jxGroups.find("group[id='" + sGroupId + "'] > ref");
										jxGroupRefs.each(function ()
										{
											var sObjectId = $(this).attr("id");
											if(CLO[sObjectId]!=oCurObj && CLO[sObjectId].sType==oCurObj.sType)
											{
												if(CLO[sObjectId].sCurrentState!="default")
												{
													CLO[sObjectId].SetState({ sState: "default" })
												}
											}
										});
									}
								}
							}
							else
							{
								oCurObj.SetState({ sState: "default" });
							}
							break;
						}
					}
				}
				return true;
			}

function RefreshText(argobj)
{
	var oCurObj = CLO[argobj.pid];
	var jCurObjDiv = $(oCurObj.div);
	var jState;
	for(var sState in oCurObj.oStates)
	{
		jState = jCurObjDiv.find("div[data-state='" + sState + "']");
		jState.find(".cl-btn-text").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
	}
	CLO[argobj.pid].FireEvent({ sName: "onRefresh" });
	return true;
}

function ResetText(argobj)
{
	var oCurObj = CLO[argobj.pid];
	var jCurObjDiv = $(oCurObj.div);
	var jState;
	for(var sState in oCurObj.oStates)
	{
		oCurObj.oStates[sState].sText = (sState=="default") ? oCurObj.jxParams.children("text_main").text() : oCurObj.oStates[sState].jxParams.children("text_main").text();
		jState = jCurObjDiv.find("div[data-state='" + sState + "']");
		jState.find(".cl-btn-text").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
	}
	oCurObj.FireEvent({ sName: "onReset" });
	return true;
}

function AddText(argobj)
{
	var oCurObj = CLO[argobj.pid];
	var jCurObjDiv = $(oCurObj.div);
	var jState;
	var sText;
	for(var sState in oCurObj.oStates)
	{
		sText = CL.Common.AddText({ sHTML: oCurObj.oStates[sState].sText, sText: argobj.value, sWhere: argobj.where, sHow: argobj.how });
		oCurObj.oStates[sState].sText = sText;
		jCurObjDiv.find("div[data-state='" + sState + "'] .cl-btn-text").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
	}
	oCurObj.FireEvent({ sName: "onAddText" });
	return true;
}

function GetProperty(argobj)
{
	if(argobj.pid==null || argobj.property==null) return "";
	var oCurObj = CLO[argobj.pid];
	if(oCurObj==null) return "";
	switch(argobj.property.toLowerCase())
	{
		case "state":
		{
			return oCurObj.sCurrentState;
		}
		case "previoustablestate":
		{
			return oCurObj.sPreviousStableState;
		}
		case "currentstablestate":
		{
			return oCurObj.sCurrentStableState;
		}
	}
	return "";
}
